import { Link } from 'react-router-dom';

const Profile = () => {
  return (
    <div style={{ 
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      backgroundColor: '#f8fafc',
      minHeight: '100vh'
    }}>
      {/* Header - exact match to Figma */}
      <header style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '16px 24px',
        borderBottom: '1px solid #e5e7eb',
        backgroundColor: '#ffffff'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '32px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <span style={{ fontSize: '24px', color: '#22c55e' }}>ðŸŒ¾</span>
            <span style={{ 
              fontSize: '20px', 
              fontWeight: '600', 
              color: '#374151',
              letterSpacing: '-0.025em'
            }}>
              FarmLink 360
            </span>
          </div>
          <nav style={{ display: 'flex', gap: '24px' }}>
            <Link to="/marketplace" style={{ 
              color: '#6b7280', 
              textDecoration: 'none',
              fontSize: '16px',
              fontWeight: '400'
            }}>
              Marketplace
            </Link>
            <Link to="/community" style={{ 
              color: '#6b7280', 
              textDecoration: 'none',
              fontSize: '16px',
              fontWeight: '400'
            }}>
              Community
            </Link>
            <Link to="/profile" style={{ 
              color: '#374151', 
              textDecoration: 'none',
              fontSize: '16px',
              fontWeight: '500'
            }}>
              Profile
            </Link>
          </nav>
        </div>
        <button style={{
          backgroundColor: '#f3f4f6',
          color: '#374151',
          border: 'none',
          borderRadius: '8px',
          padding: '8px 16px',
          fontSize: '14px',
          fontWeight: '500',
          cursor: 'pointer'
        }}>
          Logout
        </button>
      </header>

      {/* Main Content - exact match to Figma */}
      <main style={{ 
        maxWidth: '1200px', 
        margin: '0 auto', 
        padding: '48px 24px'
      }}>
        {/* Title */}
        <h1 style={{
          fontSize: '48px',
          fontWeight: '700',
          color: '#111827',
          margin: '0 0 48px 0',
          letterSpacing: '-0.025em',
          lineHeight: '1.1'
        }}>
          Profile
        </h1>

        {/* Profile Info Section */}
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          marginBottom: '64px',
          gap: '32px'
        }}>
          <div style={{
            width: '120px',
            height: '120px',
            borderRadius: '50%',
            overflow: 'hidden',
            backgroundColor: '#f3f4f6'
          }}>
            <img 
              src="https://images.unsplash.com/photo-1595273670150-bd0c3c392e46?w=120&h=120&fit=crop&crop=face" 
              alt="Michael Brown"
              style={{ 
                width: '100%', 
                height: '100%', 
                objectFit: 'cover' 
              }}
            />
          </div>
          <div>
            <h2 style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#111827',
              margin: '0 0 8px 0',
              letterSpacing: '-0.025em'
            }}>
              Michael Brown
            </h2>
            <p style={{
              fontSize: '18px',
              color: '#6b7280',
              margin: '0',
              fontWeight: '400'
            }}>
              michael.brown@example.com
            </p>
          </div>
        </div>

        {/* Farm Details Section */}
        <section style={{
          backgroundColor: '#ffffff',
          border: '2px solid #f3f4f6',
          borderRadius: '16px',
          padding: '40px'
        }}>
          <h3 style={{
            fontSize: '28px',
            fontWeight: '700',
            color: '#111827',
            margin: '0 0 32px 0',
            letterSpacing: '-0.025em'
          }}>
            Farm Details
          </h3>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))', 
            gap: '32px',
            marginBottom: '40px'
          }}>
            {/* Farm Name Row */}
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              paddingBottom: '24px',
              borderBottom: '1px solid #f3f4f6'
            }}>
              <span style={{
                fontSize: '18px',
                fontWeight: '500',
                color: '#374151'
              }}>
                Farm Name
              </span>
              <span style={{
                fontSize: '18px',
                fontWeight: '400',
                color: '#6b7280'
              }}>
                Green Pastures
              </span>
            </div>

            {/* Location Row */}
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              paddingBottom: '24px',
              borderBottom: '1px solid #f3f4f6'
            }}>
              <span style={{
                fontSize: '18px',
                fontWeight: '500',
                color: '#374151'
              }}>
                Location
              </span>
              <span style={{
                fontSize: '18px',
                fontWeight: '400',
                color: '#6b7280'
              }}>
                Nairobi, Kenya
              </span>
            </div>
          </div>

          {/* Edit Profile Button */}
          <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
            <button style={{
              backgroundColor: '#22c55e',
              color: '#ffffff',
              border: 'none',
              borderRadius: '12px',
              padding: '16px 32px',
              fontSize: '16px',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'background-color 0.2s ease'
            }}
            onMouseOver={(e) => e.target.style.backgroundColor = '#16a34a'}
            onMouseOut={(e) => e.target.style.backgroundColor = '#22c55e'}
            >
              Edit Profile
            </button>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Profile;
